import api from '../config/axios';

export const testConnection = async () => {
  try {
    console.log('üîç Testing frontend-backend connection...');
    console.log('üîç API base URL:', api.defaults.baseURL);
    
    // Test a simple request
    const response = await api.get('/auth/me');
    console.log('‚úÖ Connection test successful:', response.data);
    return { success: true, data: response.data };
  } catch (error) {
    console.error('‚ùå Connection test failed:', error);
    console.error('‚ùå Error response:', error.response?.data);
    console.error('‚ùå Error status:', error.response?.status);
    return { success: false, error: error.message };
  }
};

export const testLogin = async (email, password) => {
  try {
    console.log('üîç Testing login from frontend...');
    const response = await api.post('/auth/login', { email, password });
    console.log('‚úÖ Frontend login test successful:', response.data);
    return { success: true, data: response.data };
  } catch (error) {
    console.error('‚ùå Frontend login test failed:', error);
    console.error('‚ùå Error response:', error.response?.data);
    console.error('‚ùå Error status:', error.response?.status);
    return { success: false, error: error.message };
  }
};
