            </Link>{' '}
            and{' '}
            <Link to="/privacy" className="text-accent-400 hover:text-accent-300 transition-colors">
              Privacy Policy
            </Link>
          </p>
        </div>
      </div>
    </div>
  );
};

export default Login;
